import prisma from "@/lib/prisma";
import { getServerSession } from "next-auth/next";
import { authOptions } from "./auth/[...nextauth]";

export default async function site(req, res) {
  const session = await getServerSession({ req, res }, authOptions);
  if (!session) {
    res.status(401).end();
    return;
  }

  switch (req.method) {
    case "GET": {
      const { sessionId, siteId } = req.query;
      if (siteId) {
        // get individual site
        const { siteId } = req.query;
        const settings = await prisma.site.findUnique({
          where: {
            id: siteId,
          },
        });
        res.status(200).json(settings);
      } else {
        // get all sites
        const sites = await prisma.site.findMany({
          where: {
            user: {
              id: session.user.id,
            },
          },
        });
        res.status(200).json(sites);
      }
      return;
    }
    case "POST": {
      // create site
      const { name, subdomain, description, userId } = req.body;
      const sub = subdomain.replace(/[^a-zA-Z0-9/-]+/g, "");

      const response = await prisma.site.create({
        data: {
          name: name,
          description: description,
          subdomain: sub.length > 0 ? sub : cuid(),
          logo: "/logo.png",
          image: `/placeholder.png`,
          imageBlurhash:
            "data:image/png;base64,",
          user: {
            connect: {
              id: userId,
            },
          },
        },
      });
      res.status(200).json({ siteId: response.id });
      return;
    }
    case "DELETE": {
      // delete site
      const { siteId } = req.query;
      await prisma.post.deleteMany({
        where: {
          site: {
            id: siteId,
          },
        },
      });
      await prisma.site.delete({
        where: {
          id: siteId,
        },
      });
      res.status(200).end();
      return;
    }
    case "PUT": {
      // save site settings
      let {
        id,
        currentSubdomain,
        name,
        description,
        subdomain,
        image,
        imageBlurhash,
      } = req.body;

      // processing subdomain
      const sub = subdomain.replace(/[^a-zA-Z0-9/-]+/g, "");
      subdomain = sub.length > 0 ? sub : currentSubdomain;

      // update site settings
      const response = await prisma.site.update({
        where: {
          id: id,
        },
        data: {
          name,
          description,
          subdomain,
          image,
          imageBlurhash,
        },
      });

      res.status(200).json(response);
    }
    default:
      res.status(405).end();
      return;
  }
}
